mapper.select:
select income_amount consoleData from sys_income a, sys_user b where a.user_id = b.user_id and income_id = ( select max(income_id) from sys_income
and a.user_id = #{userId}
)

select sum(income_amount) consoleData from sys_income a, sys_user b
a.user_id = b.user_id and to_char(add_months(sysdate, -12), 'yyyyMM') = to_char(income_date, 'yyyyMM')
and a.user_id = #{userId}

select sum(income_amount) consoleData from sys_income a, sys_user b
a.user_id = b.user_id and to_char(add_months(sysdate, -1), 'yyyyMM') = to_char(income_date, 'yyyyMM')
and a.user_id = #{userId}

select gift_amount consoleData from sys_gift a, sys_user b where ( substr(a.gift_sender, instr(a.gift_sender, '-') + 1) = b.user_id or substr(a.gift_receiver, instr(a.gift_receiver, '-') + 1) = b.user_id ) and gift_id = ( select max(gift_id) from sys_gift a, sys_user b
substr(a.gift_sender, instr(a.gift_sender, '-') + 1) = b.user_id
and b.user_id = #{userId}
)

select gift_amount consoleData from sys_gift a, sys_user b where ( substr(a.gift_sender, instr(a.gift_sender, '-') + 1) = b.user_id or substr(a.gift_receiver, instr(a.gift_receiver, '-') + 1) = b.user_id ) and gift_id = ( select max(gift_id) from sys_gift a, sys_user b
substr(a.gift_receiver, instr(a.gift_receiver, '-') + 1) = b.user_id
and b.user_id = #{userId}
)

select to_char(login_date, 'yyyy-MM-dd hh24:mm:ss') consoleData from sys_login_log where log_id = ( select max(log_id) from sys_login_log a, sys_user b
a.user_id = b.user_id
and a.user_id = #{userId}
)

select count(1) consoleData from sys_login_log a, sys_user b
a.user_id = b.user_id and to_char(login_date, 'yyyyMM') = to_char(sysdate, 'yyyyMM')
and a.user_id = #{userId}

select count(1) consoleData from sys_login_log a, sys_user b
a.user_id = b.user_id and to_char(login_date, 'yyyy') = to_char(sysdate, 'yyyy')
and a.user_id = #{userId}

select count(1) consoleData from sys_login_log a, sys_user b
a.user_id = b.user_id
and a.user_id = #{userId}
